import { createStore } from "redux";

const counterReducer = (state = {counter: 0}, action) => {
    switch (action.type) {
        case 'INCREMENT':
            return {counter: state.counter + 1};
        case 'DECREMENT':
            return {counter: state.counter - 1};
        default:
            return {counter: state.counter};
    }
};

const store = redux.createStore(counterReducer);

export default store;
